policy_content.body:
permit(
    principal is Jans::Workload,
    action in [Jans::Action::"Update"],
    resource is Jans::Issue
)when{
    principal.sub == resource.sub
};

permit(
    principal is Jans::User,
    action in [Jans::Action::"Update"],
    resource is Jans::Issue
)when{
    principal.sub == resource.sub
};

schema.body:
namespace Jans {
  type Url = {"host": String, "path": String, "protocol": String};
  entity Access_token = {"aud": String, "exp": Long, "iat": Long, "iss": TrustedIssuer, "jti": String};
  entity Issue = {"sub": String};
  entity Role;
  entity TrustedIssuer = {"issuer_entity_id": Url};
  entity User in [Role] = {"email": String, "sub": String, "user_name": String};
  entity Workload = {"client_id": String, "iss": TrustedIssuer, "username": String, "sub": String};
  entity id_token = {"acr": String, "amr": Set<String>, "aud": String, "exp": Long, "iat": Long, "iss": TrustedIssuer, "jti": String, "sub": String};
  entity Userinfo_token  = {"sub": String, "iss": String, "jti": String, "client_id": String};
  type Context = {
    user: User,
    workload: Workload,
    access_token: Access_token,
    id_token: id_token,
    userinfo_token: Userinfo_token,
  };
  action "Update" appliesTo {
    principal: [Workload, User],
    resource: [Issue],
    context: Context
  };
}
